mapper.select:

select a.no, a.id, a.password, a.phone, a.name, a.birthday, a.gender, a.join_date, b.no, b.image, b.nickname, b.profile_contents
	from user a join profile b on a.no = b.user_no
	where a.no in (
		select d.friend_no
		from user c join friends d on c.no = d.user_no
		where id = #{id}
	);


select *
from user a left join profile b on a.no = b.user_no
where a.id = #{userId};
